const request     = require('supertest');
const expect      = require('chai').expect;
const server      = require('./lib/express');

describe('Translate', function () {

  it('base', function(done) {
    request(server)
      .post('/')
      .send({
        event: 'MODULE_EXEC',
        payload: {
          moduleId: 'translate',
          moduleParam: {
            text: undefined,
            to: undefined
          },
          registrationData: {
          }
        }
      })
      .set('X_CONVERSE_APP_TOKEN', require('../app-token'))
      .expect(200)
      .end(function(err, res) {
        expect(res.body).to.have.property('status');
        expect(res.body.status).to.equal(1);
        done();
      });
  })

  it('translate "hello" to spanish', function(done) {
    request(server)
      .post('/')
      .send({
        event: 'MODULE_EXEC',
        payload: {
          moduleId: 'translate',
          moduleParam: {
            text: "Hello, World!",
            to: "es"
          },
          registrationData: {
          }
        }
      })
      .set('X_CONVERSE_APP_TOKEN', require('../app-token'))
      .expect(200)
      .end(function(err, res) {
        expect(res.body).to.have.property('status');
        expect(res.body.status).to.equal(0);
        expect(res.body).to.have.property('value').be.an('object').to.have.property('translation').to.equal('Â¡Hola Mundo!');
        done();
      });
  })

});
